import React, {useEffect,useState} from 'react';
import axios from 'axios';
import LikeButton from '../../Components/LikeButton/LikeButton';
import arrowDownYellow from '../../Asset/Images/arrow_down_yellow.png'
import './Homepage.scss';

const typeJokes = [
  {
    code:'adult',
    title:'Adult Jokes',
    color: '#ff5b5b'
  },
  {
    code:'dad',
    title:'Dad Jokes',
    color: '#ff915b'
  },
  {
    code:'christmas',
    title:'Christmas Jokes',
    color: '#ffbe5b'
  },
  {
    code:'job',
    title:'Job Jokes',
    color: '#ffdf5b'
  },
  {
    code:'birthday',
    title:'Birthday Jokes',
    color: '#8fe360'
  },
  {
    code:'social',
    title:'Social Jokes',
    color: '#57e690'
  },
  {
    code:'pun',
    title:'Puns',
    color: '#57dbe6'
  },
  {
    code:'all',
    title:'View all',
    color: '',
  }
]

const Homepage = () => {
  const [titleJokes, setTitleJokes] = useState({wannaGet: 'half',arrJoke: []})
  useEffect(()=>{axios({      
      url: 'https://api.chucknorris.io/jokes/categories',     
    })
      .then(function (response) {
        const halfAmount = response.data.length / (titleJokes.wannaGet==='half' ? 2 : 1);
        const arrJokeToShow = response.data.filter((element,index)=> index<halfAmount);
        setTitleJokes({...titleJokes,arrJoke: [...arrJokeToShow]})
      });      
  },[titleJokes])
  return (
    <div className="homepage">      
      <div className='container'>
        <div className='homepage__tab'>
          {
            titleJokes.arrJoke.map((element,index)=>(
              <div key={index} className='tab__item' style={{backgroundColor: `#ff5b5b`}}>
                {element}                
              </div>
            ))
          }
        </div>
      </div>
    </div>
  );
}

export default Homepage;
