import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";


const titleJoke = createSlice({
  name: "titleJoke",
  initialState: {
    allTitle:[],
    halfTitle: []
  },
  reducers: {
    addAllTitle: (state, action) => {      
      console.log(action.payload);
      state.allTitle.concat(action.payload)
      return state;     
    },
    addHalfTitle: (state, action) =>{
      // state.halfTitle = [state.halfTitle,...action.payload];
      console.log(action.payload)
      return state;
      // state.halfTitle.concat(action.payload)
    }
  },
});

const { reducer, actions } = titleJoke;
export const { addAllTitle, addHalfTitle } = actions;
export default reducer;
