import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import LikeButton from "../../Components/LikeButton/LikeButton";
import arrowDownYellow from "../../Asset/Images/arrow_down_yellow.png";
import iconLightening from "../../Asset/Images/icon_lightening.png";
import "./Homepage.scss";

const Homepage = () => {
  const navigate = useNavigate();
  const [titleJokes, setTitleJokes] = useState({
    isHalfJokes: true,
    arrJoke: [],
    arrHalfJokes: [],
  });
  const [arrContentJokes, setArrContentJokes] = useState({
    uncategories: [],
    animal: [],
    career: [],
    celebrity: [],
    dev: [],
    explicit: [],
    family: [],
    fashion: [],
    food: [],
    history: [],
    money: [],
    movie: [],
    music: [],
    political: [],
    religion: [],
    science: [],
    sport: [],
    travel: [],
    all:[]
  });
  const [jokesToShow, setJokesToShow] = useState({
    currentCategory: "all",
    currentAmout: 1,
    currentJokesArr: [],
  });
  const handleLoadMore = () =>{
    const getCurrentAount = jokesToShow.currentAmout;
    const getCurrentCategory = jokesToShow.currentCategory;
    const getCurrentJokesArr = jokesToShow.currentJokesArr;
    const addJokesMore = arrContentJokes[getCurrentCategory].slice(getCurrentAount*9,(getCurrentAount+1)*9);    
    setJokesToShow({
      ...jokesToShow,
      currentAmout: getCurrentAount+1,
      currentJokesArr: [...getCurrentJokesArr,addJokesMore]
    })
  }
  const handleShowView = () => {
    const currentStatusTitle = titleJokes.isHalfJokes;
    setTitleJokes({ ...titleJokes, isHalfJokes: !currentStatusTitle });
  };
  const handleNavigate = (id) =>{
    navigate(`/homepage-2/${id}`)
  }
  const dispatch = useDispatch();
  useEffect(() => {
    let arrShow = [];
    const handleGetTitle = () => {
      axios({
        url: "https://api.chucknorris.io/jokes/categories",
      }).then(function(response) {
        const halfAmount = response.data.length / 2;
        const halfJokesToShow = response.data.filter(
          (element, index) => index < halfAmount
        );
        arrShow = [...response.data];
        setTitleJokes({
          ...titleJokes,
          arrJoke: [...response.data, "uncategorized"],
          arrHalfJokes: [...halfJokesToShow],
        });
      });
    };
    const handleGetContent = () => {
      axios({
        url: "https://api.chucknorris.io/jokes/search?query=all",
      }).then(function(response) {
        const classifyCategory = {
          uncategorized: [],
          animal: [],
          career: [],
          celebrity: [],
          dev: [],
          explicit: [],
          family: [],
          fashion: [],
          food: [],
          history: [],
          money: [],
          movie: [],
          music: [],
          political: [],
          religion: [],
          science: [],
          sport: [],
          travel: [],
          all:[]
        };
        console.log(response.data);
        let getTenJokes = response.data.result.slice(0, 6);
        setJokesToShow({...jokesToShow, currentJokesArr: [...jokesToShow.currentJokesArr,...getTenJokes]});
        response.data.result.forEach((element) => {
          if (!classifyCategory[element.categories] && element.categories[0]) {
            classifyCategory[element.categories] = [element];
          } else if (
            classifyCategory[element.categories] &&
            element.categories[0]
          ) {
            classifyCategory[element.categories].push(element);
          }
          if (!element.categories[0]) {
            classifyCategory["uncategorized"].push(element);
          }
          classifyCategory["all"].push(element);
        });
        setArrContentJokes({ ...arrContentJokes, ...classifyCategory });
        console.log("test", classifyCategory);
      });
    };
    handleGetTitle();
    handleGetContent();
  }, []);
  return (
    <div className="homepage">
      <div className="container">
        <div className="homepage__tab">
          {!titleJokes.isHalfJokes &&
            titleJokes.arrJoke.map((element, index) => (
              <div
                key={index}
                className="tab__item"
                style={{ backgroundColor: `#ff5b5b` }}
              >
                {element}
              </div>
            ))}
          {titleJokes.isHalfJokes &&
            titleJokes.arrHalfJokes.map((element, index) => (
              <div
                key={index}
                className="tab__item"
                style={{ backgroundColor: `#ff5b5b` }}
              >
                {element}
              </div>
            ))}
          <div className="tab__item view-all" onClick={handleShowView}>
            {titleJokes.isHalfJokes ? "View All" : "Hide"}
            <div
              className={
                titleJokes.isHalfJokes
                  ? "arrow-container"
                  : "arrow-container showing"
              }
            >
              <img src={arrowDownYellow} alt="images" className="arrow-image" />
            </div>
          </div>
        </div>
        <div className="homepage__jokes">
          <div className="container">
            <div className="jokes__wrapper">
              {jokesToShow.currentJokesArr.map((element) => (
                <div className="jokes__item" onClick={()=>{handleNavigate(element.id)}}>
                  <div className="category">
                    <img
                      src={iconLightening}
                      alt=""
                      className="icon-lightening"
                    />
                    {!element.categories.length
                      ? "Uncategorized"
                      : element.categories}
                  </div>
                  <div className="value">{element.value}</div>
                </div>
              ))}
            </div>            
            <button type="" className="jokes__load-more" onClick={handleLoadMore}>
              View More
              <img src={arrowDownYellow} alt="" />
            </button>            
          </div>
        </div>
      </div>
    </div>
  );
};

export default Homepage;
