import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { addId } from "../../Store/Model/jokeSlice";
import axios from "axios";
import arrowDownYellow from "../../Asset/Images/arrow_down_yellow.png";
import iconLightening from "../../Asset/Images/icon_lightening.png";
import "./Homepage.scss";

const Homepage = () => {
  const navigate = useNavigate();  
  const dispatch = useDispatch();
  const [titleJokes, setTitleJokes] = useState({
    isHalfJokes: true,
    arrJoke: [],
    arrHalfJokes: [],
  });
  const [arrContentJokes, setArrContentJokes] = useState({
    uncategories: [],
    animal: [],
    career: [],
    celebrity: [],
    dev: [],
    explicit: [],
    family: [],
    fashion: [],
    food: [],
    history: [],
    money: [],
    movie: [],
    music: [],
    political: [],
    religion: [],
    science: [],
    sport: [],
    travel: [],
    all:[]
  });
  const [jokesToShow, setJokesToShow] = useState({
    currentCategory: "all",
    currentAmout: 1,
    currentJokesArr: [],
  });
  const handleFilterSubject = (subject) =>{
    let getSixJokes = arrContentJokes[subject].slice(0, 6);   
    console.log(subject);     
    setJokesToShow({
      currentCategory: subject,
      currentAmout: 1,
      currentJokesArr: [...getSixJokes],
    })
  }
  const handleLoadMore = async () =>{
    const getCurrentAount = await jokesToShow.currentAmout;
    const getCurrentCategory = await jokesToShow.currentCategory;    
    const addJokesMore = await arrContentJokes[getCurrentCategory].slice(getCurrentAount*9,(getCurrentAount+1)*9);    
    console.log(addJokesMore);
    setJokesToShow({
      ...jokesToShow,
      currentAmout: getCurrentAount+1,
      currentJokesArr: [...jokesToShow.currentJokesArr,...addJokesMore],     
    })
  }
  const handleShowView = () => {
    const currentStatusTitle = titleJokes.isHalfJokes;
    setTitleJokes({ ...titleJokes, isHalfJokes: !currentStatusTitle });
  };
  const handleNavigate = (id) =>{
    dispatch(addId(id));
    navigate(`/homepage-2/${id}`)
  }

  useEffect(() => {

    const handleGetTitle = () => {
      axios({
        url: "https://api.chucknorris.io/jokes/categories",
      }).then(function(response) {
        const halfAmount = response.data.length / 2;
        const halfJokesToShow = response.data.filter(
          (element, index) => index < halfAmount
        );        
        setTitleJokes({
          ...titleJokes,
          arrJoke: [...response.data, "uncategorized"],
          arrHalfJokes: [...halfJokesToShow],
        });
      });
    };
    const handleGetContent = () => {
      axios({
        url: "https://api.chucknorris.io/jokes/search?query=all",
      }).then(function(response) {
        const classifyCategory = {
          uncategorized: [],
          animal: [],
          career: [],
