import React, {useEffect,useState} from 'react';
import axios from 'axios';
import LikeButton from '../../Components/LikeButton/LikeButton';
import arrowDownYellow from '../../Asset/Images/arrow_down_yellow.png'
import './Homepage.scss';
import { useDispatch, useSelector } from 'react-redux';

const typeJokes = [
  {
    code:'adult',
    title:'Adult Jokes',
    color: '#ff5b5b'
  },
  {
    code:'dad',
    title:'Dad Jokes',
    color: '#ff915b'
  },
  {
    code:'christmas',
    title:'Christmas Jokes',
    color: '#ffbe5b'
  },
  {
    code:'job',
    title:'Job Jokes',
    color: '#ffdf5b'
  },
  {
    code:'birthday',
    title:'Birthday Jokes',
    color: '#8fe360'
  },
  {
    code:'social',
    title:'Social Jokes',
    color: '#57e690'
  },
  {
    code:'pun',
    title:'Puns',
    color: '#57dbe6'
  },
  {
    code:'all',
    title:'View all',
    color: '',
  }
]

const Homepage = () => {
  const [titleJokes, setTitleJokes] = useState({isHalfJokes:true,arrJoke: [], arrHalfJokes: []});
  const [arrContentJokes, setArrContentJokes] = useState({})
  const handleShowView = () =>{
    const currentStatusTitle = titleJokes.isHalfJokes;   
    setTitleJokes({...titleJokes,isHalfJokes: !currentStatusTitle});    
  }
  const dispatch = useDispatch();  
  useEffect(()=>{
    let arrShow = [];
    const handleGetTitle = async() => {      
      axios({      
        url: 'https://api.chucknorris.io/jokes/categories',     
      })
        .then(function (response) {
          const halfAmount = response.data.length / 2;
          const halfJokesToShow = response.data.filter((element,index)=> index<halfAmount);
          arrShow = [...response.data]; 
          console.log('new',arrShow)         
          console.log(halfJokesToShow)
          setTitleJokes({...titleJokes,arrJoke: [...response.data], arrHalfJokes:[...halfJokesToShow]});
        });        
    }; 
    const handleGetContent = () => {
      axios({      
        url: 'https://api.chucknorris.io/jokes/search?query=all',     
      })
        .then(function (response) {
          const classifyCategory = {
            uncategorized: []
          };
          console.log(response.data)
          response.data.result.forEach(element => {
            if(!classifyCategory[element.categories]){
              classifyCategory[element.categories]=[element];
            }
            else if(classifyCategory[element.categories]){
              classifyCategory[element.categories].push(element);
            }
            else if(element){
              classifyCategory['uncategorized'].push(element);
            }
          });
          console.log(classifyCategory);
        });
    }   
    handleGetTitle();   
    handleGetContent();


  },[]);  
  return (
    <div className="homepage">      
      <div className='container'>
        <div className='homepage__tab'>
          { !titleJokes.isHalfJokes &&
            titleJokes.arrJoke.map((element,index)=>(
              <div key={index} className='tab__item' style={{backgroundColor: `#ff5b5b`}}>
                {element}                
              </div>
            ))
          }
          {titleJokes.isHalfJokes &&
            titleJokes.arrHalfJokes.map((element,index)=>(
              <div key={index} className='tab__item' style={{backgroundColor: `#ff5b5b`}}>
                {element}                
              </div>
            ))
          }
          <div className='tab__item view-all' onClick={()=>{handleShowView()}}>
              {titleJokes.isHalfJokes ? 'View All' : 'Hide'}
              <div className={titleJokes.isHalfJokes ? 'arrow-container' : 'arrow-container showing'}>
                <img src={arrowDownYellow} alt='images' className='arrow-image' />
              </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
